package net.oschina.app.ui;

import android.view.KeyEvent;

import net.oschina.app.AppConfig;
import net.oschina.app.AppContext;
import net.oschina.app.AppManager;
import net.oschina.app.widget.MyFragmentTabHost;
import butterknife.ButterKnife;
//import butterknife.InjectView;
import net.oschina.app.R;
import net.oschina.app.R.id;
import net.oschina.app.R.layout;
import net.oschina.app.R.menu;
import android.support.v7.app.ActionBarActivity;
import android.os.Bundle;
import android.view.Menu;
import android.view.MenuItem;
import android.graphics.drawable.Drawable;
import android.view.LayoutInflater;
import android.view.View;
import android.view.View.OnTouchListener;
import android.widget.TabHost.OnTabChangeListener;
import android.widget.TabHost.TabContentFactory;
import android.widget.TabHost.TabSpec;
import android.widget.TextView;

public class MainActivity extends ActionBarActivity {

	//@InjectView(android.R.id.tabhost)
    public MyFragmentTabHost mTabHost;
	
	private DoubleClickExitHelper mDoubleClickExit;
	
	@Override
	protected void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);		
		
    	setContentView(R.layout.activity_main);
		AppManager.getAppManager().addActivity(this);
		initView();
	}

	//@Override
    public void initView() {   
    	/////
    	View indicator =LayoutInflater.from(getApplicationContext())
    	        .inflate(R.layout.activity_main, null);
    	mTabHost =(MyFragmentTabHost) indicator.findViewById(android.R.id.tabhost);
    	
    	
        mDoubleClickExit = new DoubleClickExitHelper(this);
        
        mTabHost.setup(this, getSupportFragmentManager(), R.id.realtabcontent);
        if (android.os.Build.VERSION.SDK_INT > 10) {
            mTabHost.getTabWidget().setShowDividers(0);
        }
        
        initTabs();
        mTabHost.setCurrentTab(0);
	}
	/**
     * --
     */
    @Override
    public boolean onKeyDown(int keyCode, KeyEvent event) {
        if (keyCode == KeyEvent.KEYCODE_BACK) {
            // 
            if (AppContext.get(AppConfig.KEY_DOUBLE_CLICK_EXIT, true)) {
                return mDoubleClickExit.onKeyDown(keyCode, event);
            }
        }
        return super.onKeyDown(keyCode, event);
    }
    private void initTabs() {
    	MainTab[] tabs = MainTab.values();
    	final int size = tabs.length;
        for (int i = 0; i < size; i++) {
            MainTab mainTab = tabs[i];
            TabSpec tab = mTabHost.newTabSpec(getString(mainTab.getResName()));
            View indicator = LayoutInflater.from(getApplicationContext())
                    .inflate(R.layout.tab_indicator, null);
            TextView title = (TextView) indicator.findViewById(R.id.tab_title);
            Drawable drawable = this.getResources().getDrawable(
                    mainTab.getResIcon());
            title.setCompoundDrawablesWithIntrinsicBounds(null, drawable, null,
                    null);
            if (i == 2) {
                indicator.setVisibility(View.INVISIBLE);
                mTabHost.setNoTabChangedTag(getString(mainTab.getResName()));
            }
            title.setText(getString(mainTab.getResName()));
            tab.setIndicator(indicator);
            tab.setContent(new TabContentFactory() {

                @Override
                public View createTabContent(String tag) {
                    return new View(MainActivity.this);
                }
            });
            mTabHost.addTab(tab, mainTab.getClz(), null);
    }
  }
    
}
